module.exports = [{"name":"ux-input.css","body":":host {\r\n  box-sizing: border-box;\r\n  font-size: 16px;\r\n  position: relative;\r\n  display: inline-block; \r\n  vertical-align: text-bottom; }\r\n\r\n:host * {\r\n  box-sizing: border-box; } \r\n\r\n:host(.is-hidden) {\r\n  display: none; }\r\n  \r\n  input {\r\n    padding: 0 .85rem;\r\n    height: 3rem;\r\n    font-size: 1rem;\r\n    outline: none;\r\n    border: 1px solid #dfdfdf;\r\n    width: 100%;\r\n    color: #444;\r\n    display: block;\r\n    border-radius: 3px;\r\n    transition: all 0.2s ease-in-out; }\r\n\r\n  input:focus {\r\n    border-color: #298eea; }\r\n\r\n  :host(.is-active) input {\r\n    padding: 1.3rem 0.85rem 0.45rem 0.85rem; }\r\n\r\n  :host(.is-invalid) input {\r\n    border-color: rgb(200, 80, 80);\r\n    background: rgb(255,240,240); }\r\n\r\n  label {\r\n    position: absolute;\r\n    top: 6px;\r\n    padding: 0 5px;\r\n    left: 10px;\r\n    font-size: 0.7rem;\r\n    font-weight: bold;\r\n    color: #bbbbbb;\r\n    display: block;\r\n    opacity: 0;\r\n    transition: all 0.2s ease-in-out; }\r\n  \r\n  :host(.is-focused) label {\r\n    color: #298eea; }\r\n\r\n  :host(.is-active) label {\r\n    opacity: 1 }\r\n\r\n  :host(.is-optional)::after {\r\n    position: absolute;\r\n    right: 5px;\r\n    top: 3px;\r\n    font-size: 0.7em;\r\n    color: #ccc;\r\n    content: 'optional'; }"},{"name":"ux-input","body":require('./ux-input.js').default || require('./ux-input.js'),"headers":{"needs":"[css]"}}]